import React, { useState } from 'react'; 
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Assuming you're using React Router for navigation
import './Login.css';

function Login({ onLogin }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate(); // Use useNavigate for navigation

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (username && password) {
      try {
        // Attempt to update user data (assuming a user already exists with that username)
        const response = await axios.put(`http://localhost:8000/users/${username}`, {
          username,
          password,
        });

        if (response.status === 200) {
          setError(''); // Clear errors
          onLogin(); // Trigger login callback
          navigate('/dashboard'); // Navigate to the dashboard after successful login
        } else {
          setError('Invalid login credentials.');
        }
      } catch (error) {
        setError('Error occurred during login.');
        console.error('Error logging in:', error);
      }
    } else {
      setError('Please enter a valid username and password.');
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      {error && <p className="error-message">{error}</p>}
      <form className="login-form" onSubmit={handleSubmit}>
        <label>
          Username:
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </label>
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </label>
        <button type="submit">Login</button>
      </form>

      {/* Sign Up Link */}
      <p className="signup-text">
        Don't have an account? <span onClick={() => navigate('/signup')}>Sign up here</span>
      </p>
    </div>
  );
}

export default Login;
