//import React from 'react';
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './Header';
import Footer from './Footer';
import Login from './Login';
import SignUp from './SignUp';
import DataEntryForm from './DataEntryForm';
import TransactionHistory from './TransactionHistory';
import Report from './Report';
import Alert from './Alert';
import Dashboard from './Dashboard';
import ProtectedRoute from './ProtectedRoute';
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); 
  const handleLogin = () => {
    setIsLoggedIn(true); // Update login status when the user logs in
    // You may want to store this status in local storage or context for persistence
  };

  return (
    <Router>
      <div className="app-container">
        <Header />
        <div className="content">
          <Routes>
            {/* The default route for the login page */}
            {/* <Route path="/login" element={<Login onLogin={handleLogin}/>}/>
            <Route path="/" element={<Login />} />
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<SignUp />} /> */}
            <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/signup" element={<SignUp />} />
        <Route path="/dashboard" element={isLoggedIn ? <Dashboard /> : <Login />} />
            {/* Protected routes (only accessible after login) */}
            <Route path="/dashboard" element={<ProtectedRoute component={<Dashboard />} />} />
            <Route path="/data-entry" element={<ProtectedRoute component={<DataEntryForm />} />} />
            <Route path="/transaction-history" element={<ProtectedRoute component={<TransactionHistory />} />} />
            <Route path="/report" element={<ProtectedRoute component={<Report />} />} />
            <Route path="/alert" element={<ProtectedRoute component={<Alert />} />} />
          </Routes>
        </div>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
