import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';

function Dashboard() {
  const [username, setUsername] = useState('');
  const [profileImage, setProfileImage] = useState('');
  const [totalIncome, setTotalIncome] = useState(0);
  const [totalExpense, setTotalExpense] = useState(0);
  const navigate = useNavigate();

  // Fetch user data and transactions when the component mounts
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const userResponse = await axios.get('http://localhost:8000/users/1'); // Assuming user ID 1 is logged in
        const { username, profileImage } = userResponse.data;
        setUsername(username);
        setProfileImage(profileImage);

        const transactionsResponse = await axios.get('http://localhost:8000/transactions');

        // Calculate total income and expense directly
        const income = transactionsResponse.data
          .filter(t => t.type === 'income')
          .reduce((acc, t) => acc + t.amount, 0);
        const expense = transactionsResponse.data
          .filter(t => t.type === 'expense')
          .reduce((acc, t) => acc + t.amount, 0);

        setTotalIncome(income);
        setTotalExpense(expense);
      } catch (err) {
        console.error('Error fetching data:', err);
      }
    };

    fetchUserData();
  }, []);

  // Logout and redirect to login
  // const handleLogout = () => {
  //   localStorage.removeItem('isAuthenticated'); // Clear authentication status
  //   navigate('/login'); // Redirect to login
  // };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <div className="user-info">
          {/* <img src={profileImage} alt={`${username}'s profile`} className="profile-image" />
          <span>Welcome, {username}</span> */}
        </div>
        <button className="logout-button" onClick={handleLogout}>Logout</button>
      </header>

      <h2>Dashboard</h2>
      <div className="dashboard-cards">
        <div className="dashboard-card">
          <h3>Total Income</h3>
          <p>${totalIncome.toFixed(2)}</p>
        </div>
        <div className="dashboard-card">
          <h3>Total Expense</h3>
          <p>${totalExpense.toFixed(2)}</p>
        </div>
      </div>

      <div className="dashboard-quick-links">
        <h3>Quick Links</h3>
        <ul>
          <li><a href="/data-entry">Data Entry</a></li>
          <li><a href="/transaction-history">Transaction History</a></li>
          <li><a href="/reports">Reports</a></li>
          <li><a href="/alerts">Alerts</a></li>
        </ul>
      </div>
    </div>
  );
}

export default Dashboard;
