import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Header from './Header';
import Footer from './Footer';
import Login from './Login';
import SignUp from './SignUp';
import DataEntryForm from './DataEntryForm';
import TransactionHistory from './TransactionHistory';
import Report from './Report';
import Alert from './Alert';
import Dashboard from './Dashboard';
import ProtectedRoute from './ProtectedRoute';
import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleLogin = () => {
    localStorage.setItem('isAuthenticated', true); // Store the login state
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('isAuthenticated'); // Remove the login state
    setIsAuthenticated(false);
  };

  return (
    <Router>
      <div className="app-container">
        <Header onLogout={handleLogout} isAuthenticated={isAuthenticated} />
        <div className="content">
          <Routes>
            {/* Redirect to dashboard if logged in, otherwise show Login */}
            <Route 
              path="/login" 
              element={isAuthenticated ? <Navigate to="/dashboard" /> : <Login onLogin={handleLogin} />} 
            />
            <Route path="/signup" element={<SignUp />} />

            {/* Protected Routes */}
            <Route 
              path="/dashboard" 
              element={isAuthenticated ? <Dashboard /> : <Navigate to="/login" />} 
            />
            <Route 
              path="/data-entry" 
              element={isAuthenticated ? <DataEntryForm /> : <Navigate to="/login" />} 
            />
            <Route 
              path="/transaction-history" 
              element={isAuthenticated ? <TransactionHistory /> : <Navigate to="/login" />} 
            />
            <Route 
              path="/report" 
              element={isAuthenticated ? <Report /> : <Navigate to="/login" />} 
            />
            <Route 
              path="/alert" 
              element={isAuthenticated ? <Alert /> : <Navigate to="/login" />} 
            />
          </Routes>
        </div>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
