import React, { useState } from 'react';
import axios from 'axios';
import './Dashboard.css';

function Dashboard() {
  const [totalIncome, setTotalIncome] = useState(0);
  const [totalExpense, setTotalExpense] = useState(0);
  const [userId] = useState(1); // Assuming user ID is fixed at 1 for this example

  // Function to fetch income and expense data manually
  const fetchData = () => {
    const transactionsRequest = axios.get('http://localhost:8000/transactions');

    transactionsRequest
      .then((response) => {
        const transactions = response.data;

        // Calculate total income and expense
        const income = transactions
          .filter((t) => t.type === 'income')
          .reduce((acc, t) => acc + t.amount, 0);
        const expense = transactions
          .filter((t) => t.type === 'expense')
          .reduce((acc, t) => acc + t.amount, 0);

        setTotalIncome(income);
        setTotalExpense(expense);
      })
      .catch((err) => {
        console.error('Error fetching data:', err);
      });
  };

  // Example: Update user data (you can call this function when needed)
  const updateUserData = (updatedData) => {
    axios
      .put(`http://localhost:8000/users/${userId}`, updatedData)
      .then((response) => {
        console.log('User data updated:', response.data);
      })
      .catch((error) => {
        console.error('Error updating user data:', error);
      });
  };

  return (
    <div className="dashboard-container">
      <h2>Dashboard</h2>
      <div className="dashboard-cards">
        <div className="dashboard-card">
          <h3>Total Income</h3>
          <p>${totalIncome.toFixed(2)}</p>
        </div>
        <div className="dashboard-card">
          <h3>Total Expense</h3>
          <p>${totalExpense.toFixed(2)}</p>
        </div>
      </div>

      {/* Button to manually update data */}
      <button onClick={fetchData}>Update Income & Expense</button>
    </div>
  );
}

export default Dashboard;
