import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './Header';
import Footer from './Footer';
import Login from './Login';
import SignUp from './SignUp';
import Dashboard from './Dashboard';
import ProtectedRoute from './ProtectedRoute'; // Import the ProtectedRoute component
import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleLogin = () => {
    setIsAuthenticated(true); // Set authenticated status
  };

  const handleLogout = () => {
    setIsAuthenticated(false); // Reset authentication status
    navigate('/login'); // Optionally navigate to login page
  };

  return (
    <Router>
      <div className="app-container">
        <Header onLogout={handleLogout} />
        <div className="content">
          <Routes>
            <Route path="/login" element={<Login onLogin={handleLogin} />} />
            <Route path="/signup" element={<SignUp />} />
            <Route 
              path="/dashboard" 
              element={<ProtectedRoute element={<Dashboard />} isAuthenticated={isAuthenticated} />} 
            />
            {/* Other routes can be added here */}
          </Routes>
        </div>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
