import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DataEntryForm from './DataEntryForm';
import './TransactionHistory.css';

function TransactionHistory() {
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await axios.get('http://localhost:8000/transactions');
        setTransactions(response.data);
      } catch (error) {
        console.error('Error fetching transactions:', error);
      }
    };

    fetchTransactions();
  }, []);

  const handleAddTransaction = (newTransaction) => {
    setTransactions((prevTransactions) => [...prevTransactions, newTransaction]);
  };

  const handleDeleteTransaction = async (id) => {
    try {
      await axios.delete(`http://localhost:8000/transactions/${id}`);
      setTransactions((prevTransactions) => prevTransactions.filter(tx => tx.id !== id));
    } catch (error) {
      console.error('Error deleting transaction:', error);
    }
  };

  return (
    <div className="transaction-history-container">
      <h2>Transaction History</h2>
      <DataEntryForm onAddTransaction={handleAddTransaction} />
      <ul>
        {transactions.map((transaction) => (
          <li key={transaction.id}>
            <span>{transaction.type} - ${transaction.amount} - {transaction.description}</span>
            <button onClick={() => handleDeleteTransaction(transaction.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TransactionHistory;
