import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import Header from './Header';
import Footer from './Footer';
//import Login from './Login';
//import SignUp from './SignUp';
import DataEntryForm from './DataEntryForm';
import TransactionHistory from './TransactionHistory';
import Report from './Report';
import Alert from './Alert';
import Dashboard from './Dashboard';
import './App.css';

function App() {
  return (
    <Router>
      <div className="app-container">
        <Header />
        <div className="content">
          <Routes>
            {/* The default route for the login page */}
            {/* <Route path="/" element={<Login />} />
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<SignUp />} />
            <Route path="/login" element={<Login />} /> */}
            {/* Protected routes (only accessible after login) */}
            <Route path="/dashboard" element={<ProtectedRoute component={<Dashboard />} />} />
            <Route path="/data-entry" element={<ProtectedRoute component={<DataEntryForm />} />} />
            <Route path="/transaction-history" element={<ProtectedRoute component={<TransactionHistory />} />} />
            <Route path="/report" element={<ProtectedRoute component={<Report />} />} />
            <Route path="/alert" element={<ProtectedRoute component={<Alert />} />} />
          </Routes>
        </div>
        <Footer />
      </div>
    </Router>
  );
}

function ProtectedRoute({ component }) {
  const navigate = useNavigate();

  // Replace this with your actual authentication logic
  const isAuthenticated = localStorage.getItem('isAuthenticated'); // Example of storing login status

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login'); // Redirect to login if not authenticated
    }
  }, [isAuthenticated, navigate]);

  return isAuthenticated ? component : null;
}

export default App;
