import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Dashboard.css';

function Dashboard() {
  const [totalIncome, setTotalIncome] = useState(0);
  const [totalExpense, setTotalExpense] = useState(0);
  const [userId] = useState(1); // Assuming user ID is fixed at 1 for this example

  // Fetch user data and transactions when the component mounts
  useEffect(() => {
    const fetchData = () => {
      // Fetch user data and transactions concurrently
      const userRequest = axios.get(`http://localhost:8000/users/${userId}`);
      const transactionsRequest = axios.get('http://localhost:8000/transactions');

      Promise.all([userRequest, transactionsRequest])
        .then((responses) => {
          const transactionsResponse = responses[1];

          // Calculate total income and expense directly
          const income = transactionsResponse.data
            .filter(t => t.type === 'income')
            .reduce((acc, t) => acc + t.amount, 0);
          const expense = transactionsResponse.data
            .filter(t => t.type === 'expense')
            .reduce((acc, t) => acc + t.amount, 0);

          setTotalIncome(income);
          setTotalExpense(expense);
        })
        .catch((err) => {
          console.error('Error fetching data:', err);
        });
    };

    fetchData();
  }, [userId]);

  // Function to update user data
  const updateUserData = (updatedData) => {
    axios.put(`http://localhost:8000/users/${userId}`, updatedData)
      .then((response) => {
        console.log('User data updated:', response.data);
      })
      .catch((error) => {
        console.error('Error updating user data:', error);
      });
  };

  // Example: Update user data (you can call this function when needed)
  const handleUpdateUser = () => {
    const updatedData = {
      username: 'NewUsername', // Example updated value
      // Add other fields as necessary
    };
    updateUserData(updatedData);
  };

  return (
    <div className="dashboard-container">
      <h2>Dashboard</h2>
      <div className="dashboard-cards">
        <div className="dashboard-card">
          <h3>Total Income</h3>
          <p>${totalIncome.toFixed(2)}</p>
        </div>
        <div className="dashboard-card">
          <h3>Total Expense</h3>
          <p>${totalExpense.toFixed(2)}</p>
        </div>
      </div>

      {/* Example button to update user data */}
      <button onClick={handleUpdateUser}>Update User Data</button>
    </div>
  );
}

export default Dashboard;
