import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TransactionHistory = () => {
  const [transactions, setTransactions] = useState([]);
  const [newTransaction, setNewTransaction] = useState({ id: '', amount: '', description: '' });
  const [editTransaction, setEditTransaction] = useState(null);

  const API_URL = 'https://your-api-url.com/transactions'; // replace with your API URL

  // GET: Fetch all transactions
  const fetchTransactions = async () => {
    try {
      const response = await axios.get(API_URL);
      setTransactions(response.data);
    } catch (error) {
      console.error('Error fetching transactions:', error);
    }
  };

  // POST: Add new transaction
  const addTransaction = async () => {
    try {
      const response = await axios.post(API_URL, newTransaction);
      setTransactions([...transactions, response.data]);
      setNewTransaction({ id: '', amount: '', description: '' });
    } catch (error) {
      console.error('Error adding transaction:', error);
    }
  };

  // PUT: Update existing transaction
  const updateTransaction = async (transactionId) => {
    try {
      const response = await axios.put(`${API_URL}/${transactionId}`, editTransaction);
      setTransactions(
        transactions.map((transaction) =>
          transaction.id === transactionId ? response.data : transaction
        )
      );
      setEditTransaction(null);
    } catch (error) {
      console.error('Error updating transaction:', error);
    }
  };

  // DELETE: Remove transaction
  const deleteTransaction = async (transactionId) => {
    try {
      await axios.delete(`${API_URL}/${transactionId}`);
      setTransactions(transactions.filter((transaction) => transaction.id !== transactionId));
    } catch (error) {
      console.error('Error deleting transaction:', error);
    }
  };

  // Load transactions on component mount
  useEffect(() => {
    fetchTransactions();
  }, []);

  return (
    <div>
      <h1>Transaction History</h1>
      
      <h2>Add New Transaction</h2>
      <input
        type="text"
        placeholder="Amount"
        value={newTransaction.amount}
        onChange={(e) => setNewTransaction({ ...newTransaction, amount: e.target.value })}
      />
      <input
        type="text"
        placeholder="Description"
        value={newTransaction.description}
        onChange={(e) => setNewTransaction({ ...newTransaction, description: e.target.value })}
      />
      <button onClick={addTransaction}>Add Transaction</button>

      <h2>Transactions List</h2>
      <ul>
        {transactions.map((transaction) => (
          <li key={transaction.id}>
            {transaction.amount} - {transaction.description}
            <button onClick={() => setEditTransaction(transaction)}>Edit</button>
            <button onClick={() => deleteTransaction(transaction.id)}>Delete</button>
          </li>
        ))}
      </ul>

      {editTransaction && (
        <div>
          <h2>Edit Transaction</h2>
          <input
            type="text"
            placeholder="Amount"
            value={editTransaction.amount}
            onChange={(e) => setEditTransaction({ ...editTransaction, amount: e.target.value })}
          />
          <input
            type="text"
            placeholder="Description"
            value={editTransaction.description}
            onChange={(e) => setEditTransaction({ ...editTransaction, description: e.target.value })}
          />
          <button onClick={() => updateTransaction(editTransaction.id)}>Update Transaction</button>
          <button onClick={() => setEditTransaction(null)}>Cancel</button>
        </div>
      )}
    </div>
  );
};

export default TransactionHistory;
